import { Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import Form from './Components/Form/Form';
import NavBar from './Components/Navbar/NavBar';


function App() {

  const {pathname} = useLocation // 

  return (
    <div className="App">


      { pathname !== "/" && <NavBar/> } 

      <Routes>

        <Route path='/' element={<Form/>} />

      </Routes>

    </div>
  );
}

export default App;
