const Validation = ( user, errors, setErrors ) => {

    // Para el Username
    if(!user.username) setErrors({...errors, username: "Por favor completa este campo"});
    else if (user.username.length > 35) setErrors({...errors, username:"Por favor no puede superar los 35 caracteres" });
    else if ( !(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(user.username)) ) setErrors({...errors, username: "Email invalido"});
    else setErrors({...errors, username:""});

    // Para la Password
    if(!/^(?=\w*\d)(?=\w*[A-Z])(?=\w*[a-z])\S{6,10}$/.test(user.password)) setErrors({...errors, password: "Password invalida, o no cumple con algun requisito" })
    else setErrors({...errors, password:""});
}

export default Validation;
