    const [isFav, setIsFav] = useState(false);

   const handleFavorite = () => {
      if(isFav){
         setIsFav(false);
         deleteFavorite(id);
      } else {
         setIsFav(true);
         addFavorite({ 
            id,
            name, 
            species, 
            gender, 
            image, 
            onClose, 
            addFavorite, 
            deleteFavorite
         }
         )
      }
   }
